let duration = context.get('duration') || 0;

// Si el mensaje proviene del slider, actualiza el valor de duración
if (msg.topic === "slider") {
    duration = msg.payload;
    context.set('duration', duration);
    return null;  // No envía mensaje aún
}

// Si el mensaje proviene del botón, envía el valor de duración almacenado
if (msg.topic === "button") {
    msg.payload = duration;
    return msg;
}

if (msg.topic === "miniinvernadero/temperatura") {
    var temperatura = parseFloat(msg.payload);
    if (temperatura > 25) {
        var now = new Date();
        var timestamp = now.getFullYear() + 
                        ('0' + (now.getMonth() + 1)).slice(-2) + 
                        ('0' + now.getDate()).slice(-2) + '-' + 
                        ('0' + now.getHours()).slice(-2) + 
                        ('0' + now.getMinutes()).slice(-2) + 
                        ('0' + now.getSeconds()).slice(-2);
        msg.payload = { [timestamp]: temperatura }; // Clave es la fecha, valor es la temperatura
        return msg;
    }
}
return null; // No

