var data = msg.payload;
var tableData = [];

for (var id in data) {
    if (data.hasOwnProperty(id)) {
        for (var timestamp in data[id]) {
            if (data[id].hasOwnProperty(timestamp)) {
                var dateTime = timestamp.split('-');
                var date = dateTime[0];
                var time = dateTime[1];
                var formattedDate = date.slice(0, 4) + '-' + date.slice(4, 6) + '-' + date.slice(6, 8);
                var formattedTime = time.slice(0, 2) + '-' + time.slice(2, 4) + '-' + time.slice(4, 6);
                tableData.push({ "ID": id, "Fecha": formattedDate, "Hora": formattedTime, "Valor": data[id][timestamp] });
            }
        }
    }
}

msg.payload = tableData;
